package com.spd.bosipay;

import android.app.Activity;
import android.os.Bundle;
import android.view.View;
import android.widget.Button;
import android.widget.TextView;

import com.spd.base.Datautils;
import com.spd.base.SystemManager;

public class BositestActivity extends Activity implements View.OnClickListener {

    /**
     * 测试
     */
    private Button mBtnTest;
    private TextView mTvShow;
    BosiPayJni bosiPayJni;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_bositest);
//        String apkRoot="chmod 777 "+getPackageCodePath();
//        SystemManager.RootCommand(apkRoot);
        initView();
        bosiPayJni = new BosiPayJni();
    }

    private void initView() {
        mBtnTest = findViewById(R.id.btn_test);
        mBtnTest.setOnClickListener(this);
        mTvShow = findViewById(R.id.tv_show);
    }

    @Override
    public void onClick(View v) {
        int i = v.getId();
        if (i == R.id.btn_test) {
            int res = bosiPayJni.SetCertFilePath("/storage/sdcard0/bosi/");
            mTvShow.append("设置路径：" + res + "\n");
//            String re = bosiPayJni.getCertVer();
//            mTvShow.append("获取证书：" + re + "\n");
            res = bosiPayJni.UpdateCert(Datautils.HexString2Bytes(key1));
            mTvShow.append("更新证书结果：" + res + "\n");
            res = bosiPayJni.UpdateCert(Datautils.HexString2Bytes(key2));
            mTvShow.append("更新证书结果：" + res + "\n");
            res = bosiPayJni.UpdateCert(Datautils.HexString2Bytes(key3));
            mTvShow.append("更新证书结果：" + res + "\n");
            res = bosiPayJni.VerifyCode("");
            res = ~res;
            res |= 0x80000000;
            res += 1;
            mTvShow.append("验码结果：" + res + "\n");

        } else {
        }
    }

    String key1 = "";
    String key2 = "";
    String key3 = "";
}
